mapper.insert:
INSERT 
  INTO ssf_schema.users (u_id, u_pwd, u_name, u_gender, u_email, u_tell, u_birth) 
VALUES (#{u_id}, #{u_pwd}, #{u_name}, #{u_gender}, #{u_email}, #{u_tell}, #{u_birth});

mapper.select:
select * 
  from ssf_schema.users 
 where u_id = #{u_id} and u_pwd = #{u_pwd};

select *
from ssf_schema.users
where u_id = #{u_id} and u_pwd = #{u_pwd};
